const util = {
  // https://github.com/jerryc127/hexo-theme-butterfly
  diffDate: (d, more = false) => {
    const dateNow = new Date()
    const datePost = new Date(d)
    const dateDiff = dateNow.getTime() - datePost.getTime()
    const minute = 1000 * 60
    const hour = minute * 60
    const day = hour * 24
    const month = day * 30

    let result
    if (more) {
      const monthCount = dateDiff / month
      const dayCount = dateDiff / day
      const hourCount = dateDiff / hour
      const minuteCount = dateDiff / minute

      if (monthCount > 12) {
        result = null
      } else if (monthCount >= 1) {
        result = parseInt(monthCount) + ' ' + stellar.config.date_suffix.month
      } else if (dayCount >= 1) {
        result = parseInt(dayCount) + ' ' + stellar.config.date_suffix.day
      } else if (hourCount >= 1) {
        result = parseInt(hourCount) + ' ' + stellar.config.date_suffix.hour
      } else if (minuteCount >= 1) {
        result = parseInt(minuteCount) + ' ' + stellar.config.date_suffix.min
      } else {
        result = stellar.config.date_suffix.just
      }
    } else {
      result = parseInt(dateDiff / day)
    }
    return result
  },

  copy: (id, msg) => {
    const el = document.getElementById(id)
    if (el) {
      el.select()
      document.execCommand('Copy')
      if (msg && msg.length > 0) {
        hud.toast(msg)
      }
    }
  },

  toggle: (id) => {
    const el = document.getElementById(id)
    if (el) {
      el.classList.toggle('display')
    }
  },
}

const hud = {
  toast: (msg, duration) => {
    duration = isNaN(duration) ? 2000 : duration
    var el = document.createElement('div')
    el.classList.add('toast')
    el.innerHTML = msg
    document.body.appendChild(el)
    setTimeout(function () {
      var d = 0.5
      el.style.webkitTransition =
        '-webkit-transform ' + d + 's ease-in, opacity ' + d + 's ease-in'
      el.style.opacity = '0'
      setTimeout(function () {
        document.body.removeChild(el)
      }, d * 1000)
    }, duration)
  },
}

