/*exported searchFunc*/
var searchFunc = function (path, filter, searchId, contentId) {
  function getAllCombinations(keywords) {
    var i,
      j,
      result = []

    for (i = 0; i < keywords.length; i++) {
      for (j = i + 1; j < keywords.length + 1; j++) {
        result.push(keywords.slice(i, j).join(' '))
      }
    }
    return result
  }

  $.ajax({
    url: path,
    dataType: 'json',
    success: function (jsonResponse) {
      var datas = jsonResponse
      var $input = document.getElementById(searchId)
      if (!$input) {
        return
      }
      var $resultContent = document.getElementById(contentId)

      $input.addEventListener('input', function () {
        var resultList = []
        var keywords = getAllCombinations(
          this.value.trim().toLowerCase().split(' ')
        ).sort(function (a, b) {
          return b.split(' ').length - a.split(' ').length
        })
        $resultContent.innerHTML = ''
        if (this.value.trim().length <= 0) {
          return
        }
        // perform local searching
        datas.forEach(function (data) {
          if (data.title) {
            if (!data.title.trim().length) {
              return
            }
          }
          if (data.content) {
            if (!data.content.trim().length) {
              return
            }
          }
          var matches = 0
          if (filter && !data.path.includes(filter)) {
            return
          }
          var dataTitle = data.title ? data.title.trim() : ''
          var dataTitleLowerCase = dataTitle.toLowerCase()
          var dataContent = data.content
          var dataContentLowerCase = dataContent.toLowerCase()
          var dataUrl = data.path
          var indexTitle = -1
          var indexContent = -1
          var firstOccur = -1
          // only match artiles with not empty contents
          if (dataContent !== '') {
            keywords.forEach(function (keyword) {
              indexTitle = dataTitleLowerCase.indexOf(keyword)
              indexContent = dataContentLowerCase.indexOf(keyword)

              if (indexTitle >= 0 || indexContent >= 0) {
                matches += 1
                if (indexContent < 0) {
                  indexContent = 0
                }
                if (firstOccur < 0) {
                  firstOccur = indexContent
                }
              }
            })
          }
          // show search results
          if (matches > 0) {
            var searchResult = {}
            searchResult.rank = matches
            searchResult.str =
              "<li><a href='" +
              dataUrl +
              "'><span class='search-result-title'>" +
              dataTitle +
              '</span>'
            if (firstOccur >= 0) {
              // cut out 100 characters
              var start = firstOccur - 20
              var end = firstOccur + 80

              if (start < 0) {
                start = 0
              }

              if (start == 0) {
                end = 100
              }

              if (end > dataContent.length) {
                end = dataContent.length
              }

              var matchContent = dataContent.substring(start, end)

              // highlight all keywords
              var regS = new RegExp(keywords.join('|'), 'gi')
              matchContent = matchContent.replace(regS, function (keyword) {
                return '<span class="search-keyword">' + keyword + '</span>'
              })

              searchResult.str +=
                '<p class="search-result-content">' + matchContent + '...</p>'
            }
            searchResult.str += '</a></li>'
            resultList.push(searchResult)
          }
        })
        if (resultList.length) {
          resultList.sort(function (a, b) {
            return b.rank - a.rank
          })
          var result = '<ul class="search-result-list">'
          for (var i = 0; i < resultList.length; i++) {
            result += resultList[i].str
          }
          result += '</ul>'
          $resultContent.innerHTML = result
        }
      })
    },
  })
}

// Êú¨Êèí‰ª∂Áî±CardLinkÂÆöÂà∂ËÄåÊàêÔºåÂéüÈ°πÁõÆÊ∫êÁ†Å: https://github.com/Lete114/CardLink
;(() => {
  const selector = 'a.link-card[cardlink]'
  const CardLink = {
    renderer: function (el, obj) {
      var autofill = []
      const autofillStr = el.getAttribute('autofill')
      if (autofillStr) {
        autofill = autofillStr.split(',')
      }
      if (obj.title && obj.title.length > 0 && autofill.includes('title')) {
        el.querySelector('.title').innerHTML = obj.title
        el.title = obj.title
      }
      if (obj.icon && obj.icon.length > 0 && autofill.includes('icon')) {
        el.querySelector('.img').style =
          'background-image: url("' + obj.icon + '");'
        el.querySelector('.img').setAttribute('data-bg', obj.icon)
      }
      let desc = el.querySelector('.desc')
      if (
        desc &&
        obj.desc &&
        obj.desc.length > 0 &&
        autofill.includes('desc')
      ) {
        desc.innerHTML = obj.desc
      }
    },
    /**
     * Create card links
     * @param {NodeList} nodes A collection of nodes or a collection of arrays,
     * if it is an array then the array must always contain node element
     */
    setCardLink: function (nodes) {
      // If the `nodes` do not contain a `forEach` method, then the default `a[cardlink]` is used
      nodes =
        'forEach' in (nodes || {})
          ? nodes
          : document.querySelectorAll('a[cardlink]')
      nodes.forEach((el) => {
        // If it is not a tag element then it is not processed
        if (el.nodeType !== 1) return
        el.removeAttribute('cardlink')
        const api = el.getAttribute('api')
        fetch(api)
          .then(function (response) {
            if (response.ok) {
              return response.json()
            }
            throw new Error('Network response was not ok.')
          })
          .then(function (data) {
            CardLink.renderer(el, data)
          })
          .catch(function (error) {
            console.log(error)
          })
      })
    },
    init: function () {
      this.setCardLink(document.querySelectorAll(selector))
    },
  }
  stellaris.registerThemePlugin(selector, CardLink)
})()
